// src/sse.ts
import { Context } from "hono";
import { z } from "zod";

// SSE Connection management
interface SSEConnection {
  id: string;
  context: Context;
  filters: SSEFilters;
  lastEventId?: string;
  isActive: boolean;
  send: (payload: string) => boolean; // push event to client
}

// SSE Event filters
interface SSEFilters {
  projectIds?: string[];
  workPackageIds?: string[];
  toolTypes?: string[];
  eventTypes?: string[];
}

// SSE Event types
type SSEEventType = 
  | 'project_update'
  | 'work_package_update' 
  | 'time_entry_update'
  | 'tool_execution'
  | 'error'
  | 'heartbeat';

interface SSEEvent {
  id: string;
  type: SSEEventType;
  data: any;
  timestamp: string;
  projectId?: string;
  workPackageId?: string;
}

// Global connection store (in production, use Redis or similar)
const connections = new Map<string, SSEConnection>();
const encoder = new TextEncoder();
let eventCounter = 0;

// SSE subscription schema
export const sseSubscribeInput = z.object({
  projectIds: z.array(z.string()).optional(),
  workPackageIds: z.array(z.string()).optional(), 
  toolTypes: z.array(z.string()).optional(),
  eventTypes: z.array(z.string()).optional(),
  lastEventId: z.string().optional()
});

/**
 * Handle SSE connection establishment
 */
export async function handleSSEConnection(c: Context): Promise<Response> {
  const url = new URL(c.req.url);
  const filters: SSEFilters = {
    projectIds: url.searchParams.getAll('projectId'),
    workPackageIds: url.searchParams.getAll('workPackageId'),
    toolTypes: url.searchParams.getAll('toolType'),
    eventTypes: url.searchParams.getAll('eventType')
  };
  
  const lastEventId = c.req.header('Last-Event-ID');
  const connectionId = generateConnectionId();

  // Set SSE headers according to WHATWG specification
  const headers = new Headers({
    'Content-Type': 'text/event-stream; charset=utf-8',
    'Cache-Control': 'no-cache',
    'Connection': 'keep-alive',
    'Access-Control-Allow-Origin': '*',
    'Access-Control-Allow-Headers': 'Cache-Control',
    'Access-Control-Expose-Headers': 'Content-Type',
  });

  // Create readable stream for SSE
  const stream = new ReadableStream({
    start(controller) {
      let controllerOpen = true;
      const connection: SSEConnection = {
        id: connectionId,
        context: c,
        filters,
        lastEventId,
        isActive: true,
        send: (payload: string) => {
          if (!controllerOpen) {return false;}
          try {
            controller.enqueue(encoder.encode(payload));
            return true;
          } catch {
            controllerOpen = false;
            return false;
          }
        }
      };

      // Store connection
      connections.set(connectionId, connection);

      // Send initial connection event
      const initEvent = formatSSEEvent({
        id: generateEventId(),
        type: 'heartbeat',
        data: { message: 'Connected', connectionId },
        timestamp: new Date().toISOString()
      });
      
  connection.send(initEvent);

      // Send missed events if lastEventId provided
      if (lastEventId) {
        // In production, implement event replay from persistent storage
        const missedEvent = formatSSEEvent({
          id: generateEventId(),
          type: 'heartbeat',
          data: { message: 'Replay not implemented in demo', lastEventId },
          timestamp: new Date().toISOString()
        });
  connection.send(missedEvent);
      }

      // Setup heartbeat to prevent connection timeout
      const heartbeatInterval = setInterval(() => {
        if (connection.isActive) {
          const heartbeat = formatSSEEvent({
            id: generateEventId(),
            type: 'heartbeat',
            data: { timestamp: new Date().toISOString() },
            timestamp: new Date().toISOString()
          });
          
          if (!connection.send(heartbeat)) {cleanup();}
        } else {
          cleanup();
        }
      }, 30000); // 30 second heartbeat

      // Cleanup function
      const cleanup = () => {
        connection.isActive = false;
        connections.delete(connectionId);
        clearInterval(heartbeatInterval);
  try { controller.close(); } catch {}
  controllerOpen = false;
      };

      // Handle client disconnect
      c.req.raw.signal?.addEventListener('abort', cleanup);
    },

    cancel() {
      const connection = connections.get(connectionId);
      if (connection) {
        connection.isActive = false;
        connections.delete(connectionId);
      }
    }
  });

  return new Response(stream, { headers });
}

/**
 * Broadcast event to all matching connections
 */
export function broadcastSSEEvent(event: SSEEvent): void {
  const message = formatSSEEvent(event);
  connections.forEach((connection) => {
    if (!connection.isActive) {
      connections.delete(connection.id);
      return;
    }
    if (!eventMatchesFilters(event, connection.filters)) {return;}
    const ok = connection.send(message);
    if (!ok) {
      connection.isActive = false;
      connections.delete(connection.id);
    }
  });
}

/**
 * Format event according to SSE specification
 */
function formatSSEEvent(event: SSEEvent): string {
  const lines: string[] = [];
  
  // Add event ID
  if (event.id) {
    lines.push(`id: ${event.id}`);
  }
  
  // Add event type (default SSE event type is 'message')
  if (event.type) {
    lines.push(`event: ${event.type}`);
  }
  
  // Add data (can be multiple lines)
  const dataStr = typeof event.data === 'string' 
    ? event.data 
    : JSON.stringify(event.data);
    
  dataStr.split('\n').forEach(line => {
    lines.push(`data: ${line}`);
  });
  
  // Add empty line to trigger event dispatch
  lines.push('');
  
  return lines.join('\n');
}

/**
 * Check if event matches connection filters
 */
function eventMatchesFilters(event: SSEEvent, filters: SSEFilters): boolean {
  // Filter by project IDs
  if (filters.projectIds?.length && event.projectId) {
    if (!filters.projectIds.includes(event.projectId)) {
      return false;
    }
  }
  
  // Filter by work package IDs
  if (filters.workPackageIds?.length && event.workPackageId) {
    if (!filters.workPackageIds.includes(event.workPackageId)) {
      return false;
    }
  }
  
  // Filter by event types
  if (filters.eventTypes?.length) {
    if (!filters.eventTypes.includes(event.type)) {
      return false;
    }
  }
  
  return true;
}

/**
 * Generate unique connection ID
 */
function generateConnectionId(): string {
  return `sse_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
}

/**
 * Generate unique event ID
 */
function generateEventId(): string {
  return `${Date.now()}_${++eventCounter}`;
}

/**
 * Helper functions for tool integrations
 */
export function notifyProjectUpdate(projectId: string, data: any): void {
  broadcastSSEEvent({
    id: generateEventId(),
    type: 'project_update',
    data,
    timestamp: new Date().toISOString(),
    projectId
  });
}

export function notifyWorkPackageUpdate(workPackageId: string, projectId: string, data: any): void {
  broadcastSSEEvent({
    id: generateEventId(),
    type: 'work_package_update', 
    data,
    timestamp: new Date().toISOString(),
    projectId,
    workPackageId
  });
}

export function notifyToolExecution(toolName: string, projectId: string, data: any): void {
  broadcastSSEEvent({
    id: generateEventId(),
    type: 'tool_execution',
    data: { toolName, ...data },
    timestamp: new Date().toISOString(),
    projectId
  });
}

/**
 * Get connection statistics for monitoring
 */
export function getSSEStats(): { activeConnections: number; totalConnections: number } {
  const activeConnections = Array.from(connections.values()).filter(c => c.isActive).length;
  return {
    activeConnections,
    totalConnections: connections.size
  };
}

// src/tools.ts
import { z } from "zod";
import { opFetch, parseCollectionMeta, hal, withQuery } from "./util/op";

export type Ctx = { env: any };

//
// Projects
//
export const listProjectsInput = z.object({
  q: z.string().optional().describe("Optional name/identifier substring match (client-side)"),
  offset: z.number().int().min(0).default(0),
  pageSize: z.number().int().min(1).max(200).default(25),
});

export async function listProjects({ env }: Ctx, input: z.infer<typeof listProjectsInput>) {
  const { json } = await opFetch<any>(env, "/api/v3/projects", {
    params: { offset: input.offset, pageSize: input.pageSize },
  });
  // OpenProject collections carry total/count/pageSize/offset.
  const meta = parseCollectionMeta(json);
  let elements = json?._embedded?.elements ?? [];
  if (input.q) {
    const q = input.q.toLowerCase();
    elements = elements.filter((p: any) => `${p?.name || ""} ${p?.identifier || ""}`.toLowerCase().includes(q));
  }
  return { elements, ...meta };
}

//
// Types (global or per project)
//
export const listTypesInput = z.object({
  projectId: z.union([z.string(), z.number()]).optional(),
});
export async function listTypes({ env }: Ctx, input: z.infer<typeof listTypesInput>) {
  const path = input.projectId ? `/api/v3/projects/${input.projectId}/types` : "/api/v3/types";
  const { json } = await opFetch<any>(env, path);
  return json?._embedded?.elements ?? [];
}

//
// Work packages: list
//
// Stricter filter & sort schemas while still allowing raw pre-encoded strings for backward compatibility
const filterClause = z.record(z.object({ operator: z.string(), values: z.array(z.any()) }));
const filtersArraySchema = z.array(filterClause);
const sortTuple = z.tuple([z.string(), z.enum(["asc", "desc"])]);
const sortBySchema = z.array(sortTuple);
export const listWPsInput = z.object({
  projectId: z.union([z.string(), z.number()]).optional(),
  filters: z.union([z.string(), filtersArraySchema]).optional().describe("Array of filter objects or JSON string"),
  offset: z.number().int().min(0).default(0),
  pageSize: z.number().int().min(1).max(200).default(25),
  sortBy: z.union([z.string(), sortBySchema]).optional().describe('Array like [["id","asc"]] or JSON string'),
});
export async function listWorkPackages({ env }: Ctx, input: z.infer<typeof listWPsInput>) {
  const base = input.projectId ? `/api/v3/projects/${input.projectId}/work_packages` : "/api/v3/work_packages";
  const params: Record<string, unknown> = {
    offset: input.offset,
    pageSize: input.pageSize,
  };
  if (input.filters !== undefined) {params.filters = input.filters;}
  if (input.sortBy !== undefined) {params.sortBy = input.sortBy;}

  const path = withQuery(base, params);
  const { json } = await opFetch<any>(env, path);
  const meta = parseCollectionMeta(json);
  const elements = json?._embedded?.elements ?? [];
  return { elements, ...meta, _links: json?._links };
}

//
// Work package: create (forms-first)
//
export const createWPInput = z.object({
  projectId: z.union([z.string(), z.number()]),
  typeId: z.union([z.string(), z.number()]),
  subject: z.string().min(1),
  description: z
    .object({ format: z.string().default("markdown"), raw: z.string().default(""), html: z.string().optional() })
    .partial()
    .optional(),
  assigneeId: z.union([z.string(), z.number()]).optional(),
  priorityId: z.union([z.string(), z.number()]).optional(),
  parentId: z.union([z.string(), z.number()]).optional(),
  startDate: z.string().optional(), // YYYY-MM-DD
  dueDate: z.string().optional(),
  notify: z.boolean().default(false),
  dryRun: z.boolean().default(false),
});

export async function createWorkPackageViaForm({ env }: Ctx, input: z.infer<typeof createWPInput>) {
  const payload: any = {
    subject: input.subject,
    _links: {
      project: hal.project(input.projectId),
      type: hal.type(input.typeId),
    },
  };
  if (input.description) {payload.description = input.description;}
  if (input.assigneeId) {payload._links.assignee = hal.user(input.assigneeId);}
  if (input.priorityId) {payload._links.priority = hal.priority(input.priorityId);}
  if (input.parentId) {payload._links.parent = hal.workPackage(input.parentId);}
  if (input.startDate) {payload.startDate = input.startDate;}
  if (input.dueDate) {payload.dueDate = input.dueDate;}

  const { json: form } = await opFetch<any>(env, "/api/v3/work_packages/form", {
    method: "POST",
    body: JSON.stringify({ ...payload, _meta: { sendNotifications: input.notify } }),
  });
  // Forms expose "validationErrors" and a "commit" link with method (POST for create).
  const errors = form?.validationErrors ?? {};
  const hasErrors = errors && Object.keys(errors).length > 0;
  if (input.dryRun || hasErrors) {
    return { ok: !hasErrors, validationErrors: errors, payload: form?.payload, schema: form?.schema };
  }
  const commit = form?._links?.commit;
  if (!commit?.href) {throw new Error("Form commit link missing");}
  const { json: created } = await opFetch<any>(env, commit.href, {
    method: commit.method || "POST",
    body: JSON.stringify(form.payload),
  });
  return { ok: true, workPackage: created };
}

//
// Work package: update (PATCH; optional dry-run via forms)
//
export const updateWPInput = z.object({
  id: z.union([z.string(), z.number()]),
  lockVersion: z.number().int().min(0),
  subject: z.string().optional(),
  description: z.object({ format: z.string(), raw: z.string(), html: z.string().optional() }).partial().optional(),
  typeId: z.union([z.string(), z.number()]).optional(),
  statusId: z.union([z.string(), z.number()]).optional(),
  assigneeId: z.union([z.string(), z.number()]).optional(),
  priorityId: z.union([z.string(), z.number()]).optional(),
  parentId: z.union([z.string(), z.number()]).optional(),
  startDate: z.string().optional(),
  dueDate: z.string().optional(),
  notify: z.boolean().default(false),
  dryRun: z.boolean().default(false),
});

export async function updateWorkPackage({ env }: Ctx, input: z.infer<typeof updateWPInput>) {
  // Build payload for PATCH /api/v3/work_packages/{id} per spec.
  const body: any = { lockVersion: input.lockVersion };
  if (input.subject !== undefined) {body.subject = input.subject;}
  if (input.description) {body.description = input.description;}
  body._links = body._links || {};
  if (input.typeId) {body._links.type = hal.type(input.typeId);}
  if (input.statusId) {body._links.status = hal.status(input.statusId);}
  if (input.assigneeId) {body._links.assignee = hal.user(input.assigneeId);}
  if (input.priorityId) {body._links.priority = hal.priority(input.priorityId);}
  if (input.parentId) {body._links.parent = hal.workPackage(input.parentId);}
  if (input.startDate) {body.startDate = input.startDate;}
  if (input.dueDate) {body.dueDate = input.dueDate;}

  if (input.dryRun) {
    // Validate via forms mechanism (do not commit).
    const { json: form } = await opFetch<any>(env, `/api/v3/work_packages/${input.id}/form`, {
      method: "POST",
      body: JSON.stringify({ ...body, _meta: { sendNotifications: input.notify } }),
    });
    const errors = form?.validationErrors ?? {};
    const hasErrors = errors && Object.keys(errors).length > 0;
    return { ok: !hasErrors, validationErrors: errors, payload: form?.payload, schema: form?.schema };
  }

  const { json: updated } = await opFetch<any>(env, `/api/v3/work_packages/${input.id}`, {
    method: "PATCH",
    body: JSON.stringify({ ...body, _meta: { sendNotifications: input.notify } }),
  });
  return { ok: true, workPackage: updated };
}

//
// Attachments: add file to a WP (multipart/form-data)
//
export const attachInput = z.object({
  workPackageId: z.union([z.string(), z.number()]),
  fileName: z.string().min(1),
  dataBase64: z.string().min(1),
  contentType: z.string().default("application/octet-stream"),
  description: z.string().optional(),
});

const MAX_ATTACHMENT_BYTES = 5 * 1024 * 1024; // 5MB safeguard
export async function attachToWorkPackage({ env }: Ctx, input: z.infer<typeof attachInput>) {
  const b64 = input.dataBase64.trim();
  const padding = (b64.endsWith("==") ? 2 : b64.endsWith("=") ? 1 : 0);
  const approxSize = Math.floor((b64.length * 3) / 4) - padding;
  if (approxSize > MAX_ATTACHMENT_BYTES) {
    throw new Error(`Attachment exceeds max size of ${MAX_ATTACHMENT_BYTES} bytes (approx ${approxSize}).`);
  }
  const form = new FormData();
  const metadata = {
    fileName: input.fileName,
    description: input.description ? { format: "plain", raw: input.description } : undefined,
  };
  form.append("metadata", new Blob([JSON.stringify(metadata)], { type: "application/json" }), "metadata.json");
  let bytes: Uint8Array;
  try {
    bytes = Uint8Array.from(atob(b64), (c) => c.charCodeAt(0));
  } catch {
    throw new Error("Invalid base64 data");
  }
  // Cast to ArrayBuffer to satisfy TS in some environments
  const pureBuffer = new Uint8Array(bytes).buffer; // guarantee ArrayBuffer instance
  form.append("file", new Blob([pureBuffer], { type: input.contentType }), input.fileName);
  const { json } = await opFetch<any>(env, `/api/v3/work_packages/${input.workPackageId}/attachments`, {
    method: "POST",
    body: form as any,
    headers: {},
  });
  return json;
}

//
// Queries: list & run
//
export const listQueriesInput = z.object({
  // optional filters JSON for the queries collection (e.g., [{"starred":{"operator":"=","values":["t"]}}])
  filters: z.any().optional(),
  offset: z.number().int().min(0).default(0),
  pageSize: z.number().int().min(1).max(200).default(25),
});

export async function listQueries({ env }: Ctx, input: z.infer<typeof listQueriesInput>) {
  const params: Record<string, unknown> = { offset: input.offset, pageSize: input.pageSize };
  if (input.filters !== undefined) {params.filters = input.filters;}
  const { json } = await opFetch<any>(env, "/api/v3/queries", { params });
  const meta = parseCollectionMeta(json);
  const elements = json?._embedded?.elements ?? [];
  return { elements, ...meta };
}

export const runQueryInput = z.object({
  id: z.union([z.string(), z.number()]),
  // Optional overrides per spec: filters/offset/pageSize/sortBy/groupBy/showSums/timestamps.
  filters: z.any().optional(),
  offset: z.number().int().min(0).optional(),
  pageSize: z.number().int().min(1).max(200).optional(),
  sortBy: z.any().optional(),
  groupBy: z.string().optional(),
  showSums: z.boolean().optional(),
  timestamps: z.string().optional(),
});

export async function runQuery({ env }: Ctx, input: z.infer<typeof runQueryInput>) {
  const params: Record<string, unknown> = {};
  for (const k of ["filters", "offset", "pageSize", "sortBy", "groupBy", "showSums", "timestamps"] as const) {
    const v = (input as any)[k];
    if (v !== undefined) {params[k] = v;}
  }
  const { json: q } = await opFetch<any>(env, `/api/v3/queries/${input.id}`, { params });

  // If results are embedded, return them; otherwise follow the "results" link described by docs.
  if (q?._embedded?.results) {
    const meta = parseCollectionMeta(q._embedded.results);
    const elements = q._embedded.results?._embedded?.elements ?? [];
    return { query: q, results: { elements, ...meta, _links: q._embedded.results?._links } };
  }

  const href: string | undefined = q?._links?.results?.href;
  if (!href) {return { query: q, results: null };}

  const { json } = await opFetch<any>(env, href);
  const meta = parseCollectionMeta(json);
  const elements = json?._embedded?.elements ?? [];
  return { query: q, results: { elements, ...meta, _links: json?._links } };
}

// src/tools/comments.ts
import { z } from "zod";
import { opFetch } from "../util/op";
import type { Ctx } from "../tools";

/**
 * Enhanced Comments API Tools
 * 
 * Leverages OpenProject's latest internal comments capabilities including:
 * - Capabilities API checking for comment permissions
 * - Internal vs public comment support
 * - Enhanced security for team discussions
 */

// Check comment capabilities for a work package
export const checkCommentCapabilitiesInput = z.object({
  workPackageId: z.union([z.string(), z.number()])
    .describe("Work package ID to check comment capabilities for")
}).strict();

export async function checkCommentCapabilities({ env }: Ctx, input: z.infer<typeof checkCommentCapabilitiesInput>) {
  try {
    // Get work package with capabilities
    const { json: workPackage } = await opFetch<any>(env, `/api/v3/work_packages/${input.workPackageId}`);
    
    const capabilities = {
      workPackageId: input.workPackageId,
      canComment: false,
      canCreateInternalComments: false,
      canViewInternalComments: false,
      canEditComments: false,
      canDeleteComments: false,
      availableActivities: [] as string[]
    };
    
    // Check available capabilities from _links
    if (workPackage?._links) {
      const links = workPackage._links;
      
      // Check if user can add activities (comments)
      if (links.addComment || links.activities) {
        capabilities.canComment = true;
      }
      
      // Check specific internal comment capabilities
      if (links.addInternalComment) {
        capabilities.canCreateInternalComments = true;
      }
      
      // Check edit capabilities
      if (links.update || links.updateImmediately) {
        capabilities.canEditComments = true;
      }
    }
    
    // Get available activity types via capabilities API
    try {
      const { json: activitiesForm } = await opFetch<any>(
        env, 
        `/api/v3/work_packages/${input.workPackageId}/activities/form`
      );
      
      if (activitiesForm?._embedded?.schema?.activity?._embedded?.allowedValues) {
        capabilities.availableActivities = activitiesForm._embedded.schema.activity._embedded.allowedValues
          .map((activity: any) => activity.name || activity.id);
      }
      
      // Check for internal comment type
      if (capabilities.availableActivities.includes('Internal comment')) {
        capabilities.canCreateInternalComments = true;
      }
      
    } catch (error) {
      // Form might not be available, capabilities already checked via _links
    }
    
    return capabilities;
    
  } catch (error: any) {
    return {
      workPackageId: input.workPackageId,
      error: `Failed to check capabilities: ${error.message}`,
      canComment: false,
      canCreateInternalComments: false,
      canViewInternalComments: false,
      canEditComments: false,
      canDeleteComments: false,
      availableActivities: []
    };
  }
}

// Add internal comment to work package
export const addInternalCommentInput = z.object({
  workPackageId: z.union([z.string(), z.number()])
    .describe("Work package ID to add comment to"),
  comment: z.string().min(1)
    .describe("Comment text"),
  format: z.enum(['markdown', 'textile', 'plain']).default('markdown')
    .describe("Comment format"),
  internal: z.boolean().default(true)
    .describe("Make comment internal (visible only to team members)"),
  notifyWatchers: z.boolean().default(false)
    .describe("Notify watchers about this internal comment")
}).strict();

export async function addInternalComment({ env }: Ctx, input: z.infer<typeof addInternalCommentInput>) {
  try {
    // First check capabilities
    const capabilities = await checkCommentCapabilities({ env }, { workPackageId: input.workPackageId });
    
    if (!capabilities.canCreateInternalComments && input.internal) {
      return {
        success: false,
        error: "User does not have permission to create internal comments",
        capabilities
      };
    }
    
    if (!capabilities.canComment) {
      return {
        success: false,
        error: "User does not have permission to add comments",
        capabilities
      };
    }
    
    const payload = {
      comment: {
        format: input.format,
        raw: input.comment
      },
      notify: input.notifyWatchers
    };
    
    // Add internal comment type if available and requested
    if (input.internal && capabilities.availableActivities.includes('Internal comment')) {
      (payload as any).activity = {
        href: `/api/v3/activity/${capabilities.availableActivities.indexOf('Internal comment')}`
      };
    }
    
    // Create form first to validate
    const { json: form } = await opFetch<any>(
      env, 
      `/api/v3/work_packages/${input.workPackageId}/activities/form`,
      {
        method: 'POST',
        body: JSON.stringify(payload)
      }
    );
    
    if (form?.validationErrors && Object.keys(form.validationErrors).length > 0) {
      return {
        success: false,
        error: "Comment validation failed",
        validationErrors: form.validationErrors,
        capabilities
      };
    }
    
    // Commit the comment
    if (form?._links?.commit) {
      const { json: result } = await opFetch<any>(env, form._links.commit.href, {
        method: form._links.commit.method || 'POST',
        body: JSON.stringify(form.payload)
      });
      
      return {
        success: true,
        comment: {
          id: result?.id,
          workPackageId: input.workPackageId,
          comment: input.comment,
          internal: input.internal,
          createdAt: result?.createdAt,
          user: result?._links?.user,
          activity: result
        },
        capabilities
      };
    }
    
    return {
      success: false,
      error: "Could not create comment - no commit link available",
      capabilities
    };
    
  } catch (error: any) {
    return {
      success: false,
      error: error.message
    };
  }
}

// List comments with internal/public filtering
export const listCommentsInput = z.object({
  workPackageId: z.union([z.string(), z.number()])
    .describe("Work package ID to get comments for"),
  includeInternal: z.boolean().default(true)
    .describe("Include internal comments (requires permission)"),
  includePublic: z.boolean().default(true)
    .describe("Include public comments"),
  sortBy: z.array(z.tuple([
    z.enum(['id', 'createdAt', 'updatedAt']),
    z.enum(['asc', 'desc'])
  ])).default([['createdAt', 'desc']]).describe("Sort activities"),
  pageSize: z.number().int().min(1).max(100).default(20),
  offset: z.number().int().min(0).default(0)
}).strict();

export async function listComments({ env }: Ctx, input: z.infer<typeof listCommentsInput>) {
  try {
    // Check capabilities first
    const capabilities = await checkCommentCapabilities({ env }, { workPackageId: input.workPackageId });
    
    const params: any = {
      pageSize: input.pageSize,
      offset: input.offset,
      sortBy: JSON.stringify(input.sortBy)
    };
    
    // Build filters for comment types
    const filters: any[] = [];
    
    if (!input.includeInternal || !capabilities.canViewInternalComments) {
      // Exclude internal comments if not requested or no permission
      filters.push({
        type: { operator: '!', values: ['Activity::Comment-Internal'] }
      });
    }
    
    if (!input.includePublic) {
      // Exclude public comments if not requested
      filters.push({
        type: { operator: '!', values: ['Activity::Comment'] }
      });
    }
    
    if (filters.length > 0) {
      params.filters = JSON.stringify(filters);
    }
    
    const { json } = await opFetch<any>(env, `/api/v3/work_packages/${input.workPackageId}/activities`, { params });
    
    const activities = json?._embedded?.elements || [];
    
    // Filter and categorize comments
    const comments = activities
      .filter((activity: any) => 
        activity._type === 'Activity::Comment' || 
        activity._type === 'Activity::Comment-Internal'
      )
      .map((activity: any) => ({
        id: activity.id,
        type: activity._type,
        internal: activity._type === 'Activity::Comment-Internal',
        comment: activity.comment,
        createdAt: activity.createdAt,
        updatedAt: activity.updatedAt,
        user: activity._links?.user,
        canEdit: !!activity._links?.update,
        canDelete: !!activity._links?.delete,
        _links: activity._links
      }));
    
    return {
      workPackageId: input.workPackageId,
      comments,
      total: json?.total || 0,
      count: comments.length,
      pageSize: input.pageSize,
      offset: input.offset,
      capabilities,
      _links: json?._links
    };
    
  } catch (error: any) {
    return {
      workPackageId: input.workPackageId,
      error: error.message,
      comments: [],
      capabilities: null
    };
  }
}

// Update/edit a comment (if user has permission)
export const updateCommentInput = z.object({
  workPackageId: z.union([z.string(), z.number()])
    .describe("Work package ID"),
  commentId: z.union([z.string(), z.number()])
    .describe("Comment/activity ID to update"),
  comment: z.string().min(1)
    .describe("Updated comment text"),
  format: z.enum(['markdown', 'textile', 'plain']).default('markdown')
    .describe("Comment format")
}).strict();

export async function updateComment({ env }: Ctx, input: z.infer<typeof updateCommentInput>) {
  try {
    // Check if the specific comment can be edited
    const { json: activity } = await opFetch<any>(env, `/api/v3/activities/${input.commentId}`);
    
    if (!activity?._links?.update) {
      return {
        success: false,
        error: "User does not have permission to edit this comment",
        commentId: input.commentId
      };
    }
    
    const payload = {
      comment: {
        format: input.format,
        raw: input.comment
      }
    };
    
    // Update the comment
    const { json: result } = await opFetch<any>(env, activity._links.update.href, {
      method: activity._links.update.method || 'PATCH',
      body: JSON.stringify(payload)
    });
    
    return {
      success: true,
      comment: {
        id: result?.id || input.commentId,
        workPackageId: input.workPackageId,
        comment: input.comment,
        updatedAt: result?.updatedAt,
        internal: result?._type === 'Activity::Comment-Internal',
        activity: result
      }
    };
    
  } catch (error: any) {
    return {
      success: false,
      error: error.message,
      commentId: input.commentId
    };
  }
}

// Delete a comment (if user has permission)
export const deleteCommentInput = z.object({
  workPackageId: z.union([z.string(), z.number()])
    .describe("Work package ID"),
  commentId: z.union([z.string(), z.number()])
    .describe("Comment/activity ID to delete")
}).strict();

export async function deleteComment({ env }: Ctx, input: z.infer<typeof deleteCommentInput>) {
  try {
    // Check if the specific comment can be deleted
    const { json: activity } = await opFetch<any>(env, `/api/v3/activities/${input.commentId}`);
    
    if (!activity?._links?.delete) {
      return {
        success: false,
        error: "User does not have permission to delete this comment",
        commentId: input.commentId
      };
    }
    
    // Delete the comment
    await opFetch<any>(env, activity._links.delete.href, {
      method: activity._links.delete.method || 'DELETE'
    });
    
    return {
      success: true,
      commentId: input.commentId,
      workPackageId: input.workPackageId,
      deletedAt: new Date().toISOString()
    };
    
  } catch (error: any) {
    return {
      success: false,
      error: error.message,
      commentId: input.commentId
    };
  }
}

// src/tools/dependenciesEnterprise.ts
import { z } from "zod";
import { opFetch, parseCollectionMeta, hal } from "../util/op";
import type { Ctx } from "../tools";

//
// Enterprise Dependency & Critical Path Management
//

// Complete dependency types (MS Project standard)
const RelationTypeSchema = z.enum([
  'follows',      // Finish-Start (FS) - Default, B starts after A finishes
  'blocks',       // Start-Start (SS) - B starts when A starts
  'precedes',     // Finish-Finish (FF) - B finishes when A finishes  
  'duplicates',   // Duplicate relationship
  'relates',      // General relationship (no scheduling impact)
  'starts',       // Start-Finish (SF) - B finishes when A starts (rare)
]).describe("Dependency relationship type");

// Response modes (Monday.com style)
const ResponseModeSchema = z.enum([
  'flexible',  // Allow manual adjustments without cascading
  'strict',    // Automatically adjust dependent tasks
  'no_action'  // Maintain relationships but don't auto-adjust dates
]).describe("How dependent tasks respond to changes");

// Enhanced duration schema for lead/lag with negative lag support (OpenProject 2024-2025)
const DurationSchema = z.string().regex(/^-?P(?:\d+D)?(?:T(?:\d+H)?(?:\d+M)?(?:\d+S)?)?$/).optional().describe("Lead/lag duration (P2D = 2 days lag, -P1D = 1 day negative lag/lead)");

// External dependency types
const ExternalDependencyTypeSchema = z.enum([
  'vendor',        // Vendor/supplier dependency
  'client',        // Client/customer dependency  
