// src/tools/timeTrackingEnterprise.ts
import { z } from "zod";
import { opFetch, parseCollectionMeta, hal, withQuery } from "../util/op";
import type { Ctx } from "../tools";

//
// Enterprise Time Tracking & Resource Management
//

// Time format schema (HH:MM format)
const TimeSchema = z.string().regex(/^([01]?[0-9]|2[0-3]):[0-5][0-9]$/).optional().describe("Time in HH:MM format");

// Custom fields for time entries
const TimeEntryCustomFieldsSchema = z.object({
  customField1: z.any().optional(), // Overtime flag
  customField2: z.any().optional(), // Client billable
  customField3: z.any().optional(), // Department code
  customField4: z.any().optional(), // Project phase
  customField5: z.any().optional(), // Task complexity
  customField6: z.any().optional(), // Location (remote/office)
  customField7: z.any().optional(), // Cost center override
  customField8: z.any().optional(), // Approval status
}).partial();

//
// Enterprise Time Entry Creation
//
export const logTimeEntryEnterpriseInput = z.object({
  // Core time tracking
  workPackageId: z.union([z.string(), z.number()]).describe("Work package ID"),
  spentOn: z.string().describe("Date when time was spent (YYYY-MM-DD)"),
  hours: z.number().positive().max(24).describe("Hours spent (decimal allowed, max 24)"),
  comment: z.string().optional().describe("Description of work performed"),
  
  // Enterprise time management
  activityId: z.union([z.string(), z.number()]).optional().describe("Activity/category ID (Development, Testing, PM, etc.)"),
  startTime: TimeSchema.describe("Start time (HH:MM) for detailed tracking"),
  endTime: TimeSchema.describe("End time (HH:MM) for detailed tracking"),
  ongoing: z.boolean().default(false).describe("Whether this is an active timer"),
  
  // Cost accounting (enterprise)
  billableHours: z.number().min(0).max(24).optional().describe("Billable hours (may differ from actual)"),
  billingRate: z.number().min(0).optional().describe("Hourly billing rate for this entry"),
  costRate: z.number().min(0).optional().describe("Cost rate for resource accounting"),
  
  // Resource management
  userId: z.union([z.string(), z.number()]).optional().describe("User ID (defaults to current user)"),
  
  // Custom enterprise fields
  customFields: TimeEntryCustomFieldsSchema.optional().describe("Organization-specific time tracking fields"),
}).strict();

export async function logTimeEntryEnterprise({ env }: Ctx, input: z.infer<typeof logTimeEntryEnterpriseInput>) {
  // Build comprehensive time entry payload
  const payload: any = {
    spentOn: input.spentOn,
    hours: input.hours,
    _links: {
      workPackage: hal.workPackage(input.workPackageId),
    },
  };

  // Add optional core fields
  if (input.comment) {payload.comment = input.comment;}
  if (input.ongoing !== undefined) {payload.ongoing = input.ongoing;}

  // Enterprise time management
  if (input.startTime) {payload.startTime = input.startTime;}
  if (input.endTime) {payload.endTime = input.endTime;}
  if (input.activityId) {payload._links.activity = { href: `/api/v3/time_entries/activities/${input.activityId}` };}

  // Resource assignment
  if (input.userId) {payload._links.user = hal.user(input.userId);}

  // Cost accounting fields
  if (input.billableHours !== undefined) {payload.billableHours = input.billableHours;}
  if (input.billingRate !== undefined) {payload.billingRate = input.billingRate;}
  if (input.costRate !== undefined) {payload.costRate = input.costRate;}

  // Custom fields
  if (input.customFields) {
    Object.entries(input.customFields).forEach(([key, value]) => {
      if (value !== undefined) {
        payload[key] = value;
      }
    });
  }

  const { json: created } = await opFetch<any>(env, "/api/v3/time_entries", {
    method: "POST",
    body: JSON.stringify(payload),
  });

  return { 
    ok: true, 
    timeEntry: created,
    timeEntryUrl: created?._links?.self?.href,
    totalHours: created?.hours,
    billableAmount: input.billableHours && input.billingRate ? input.billableHours * input.billingRate : undefined
  };
}

//
// Time Entry Updates (Enterprise)
//
export const updateTimeEntryEnterpriseInput = z.object({
  id: z.union([z.string(), z.number()]).describe("Time entry ID"),
  
  // Updatable fields
  spentOn: z.string().optional().describe("Date when time was spent"),
  hours: z.number().positive().max(24).optional().describe("Hours spent"),
  comment: z.string().optional().describe("Work description"),
  
  // Enterprise updates
  activityId: z.union([z.string(), z.number()]).optional().describe("Activity category"),
  startTime: TimeSchema.describe("Start time"),
  endTime: TimeSchema.describe("End time"),
  ongoing: z.boolean().optional().describe("Active timer status"),
  
  // Cost updates
  billableHours: z.number().min(0).max(24).optional(),
  billingRate: z.number().min(0).optional(),
  costRate: z.number().min(0).optional(),
  
  // Custom field updates
  customFields: TimeEntryCustomFieldsSchema.optional(),
}).strict();

export async function updateTimeEntryEnterprise({ env }: Ctx, input: z.infer<typeof updateTimeEntryEnterpriseInput>) {
  const payload: any = {};

  // Update core fields
  if (input.spentOn !== undefined) {payload.spentOn = input.spentOn;}
  if (input.hours !== undefined) {payload.hours = input.hours;}
  if (input.comment !== undefined) {payload.comment = input.comment;}
  if (input.ongoing !== undefined) {payload.ongoing = input.ongoing;}
  if (input.startTime !== undefined) {payload.startTime = input.startTime;}
  if (input.endTime !== undefined) {payload.endTime = input.endTime;}

  // Update links
  payload._links = {};
  if (input.activityId !== undefined) {
    payload._links.activity = input.activityId ? { href: `/api/v3/time_entries/activities/${input.activityId}` } : null;
  }

  // Update cost fields
  if (input.billableHours !== undefined) {payload.billableHours = input.billableHours;}
  if (input.billingRate !== undefined) {payload.billingRate = input.billingRate;}
  if (input.costRate !== undefined) {payload.costRate = input.costRate;}

  // Update custom fields
  if (input.customFields) {
    Object.entries(input.customFields).forEach(([key, value]) => {
      if (value !== undefined) {
        payload[key] = value;
      }
    });
  }

  const { json: updated } = await opFetch<any>(env, `/api/v3/time_entries/${input.id}`, {
    method: "PATCH",
    body: JSON.stringify(payload),
  });

  return { 
    ok: true, 
    timeEntry: updated,
    updatedFields: Object.keys(payload)
  };
}

//
// Enterprise Time Reporting & Analytics
//
export const generateTimesheetInput = z.object({
  // Time period
  startDate: z.string().describe("Report start date (YYYY-MM-DD)"),
  endDate: z.string().describe("Report end date (YYYY-MM-DD)"),
  
  // Filtering
  projectId: z.union([z.string(), z.number()]).optional().describe("Filter by project"),
  userId: z.union([z.string(), z.number()]).optional().describe("Filter by user"),
  workPackageId: z.union([z.string(), z.number()]).optional().describe("Filter by work package"),
  activityId: z.union([z.string(), z.number()]).optional().describe("Filter by activity"),
  
  // Enterprise filtering
  billableOnly: z.boolean().default(false).describe("Show only billable hours"),
  includeCustomFields: z.boolean().default(false).describe("Include custom field data"),
  
  // Grouping and aggregation
  groupBy: z.enum(['user', 'project', 'workPackage', 'activity', 'date']).default('date').describe("Group results by"),
  includeTotals: z.boolean().default(true).describe("Include summary totals"),
  
  // Pagination
  offset: z.number().int().min(0).default(0),
  pageSize: z.number().int().min(1).max(1000).default(100),
}).strict();

export async function generateTimesheet({ env }: Ctx, input: z.infer<typeof generateTimesheetInput>) {
  const filters: any[] = [];
  
  // Date range filter (required)
  filters.push({ spentOn: { operator: ">=d", values: [input.startDate] } });
  filters.push({ spentOn: { operator: "<=d", values: [input.endDate] } });
  
  // Optional filters
  if (input.projectId !== undefined) {
    filters.push({ project: { operator: "=", values: [input.projectId.toString()] } });
  }
  
  if (input.userId !== undefined) {
    filters.push({ user: { operator: "=", values: [input.userId.toString()] } });
  }
  
  if (input.workPackageId !== undefined) {
    filters.push({ workPackage: { operator: "=", values: [input.workPackageId.toString()] } });
  }
  
  if (input.activityId !== undefined) {
    filters.push({ activity: { operator: "=", values: [input.activityId.toString()] } });
  }

  // Enterprise filtering
  if (input.billableOnly) {
    filters.push({ billableHours: { operator: ">", values: ["0"] } });
  }

  const params: Record<string, unknown> = {
    offset: input.offset,
    pageSize: input.pageSize,
    filters: JSON.stringify(filters),
  };

  // Sorting by group criteria
  const sortBy: string[][] = [];
  switch (input.groupBy) {
    case 'user':
      sortBy.push(['user', 'asc'], ['spentOn', 'asc']);
      break;
    case 'project':
      sortBy.push(['project', 'asc'], ['spentOn', 'asc']);
      break;
    case 'workPackage':
      sortBy.push(['workPackage', 'asc'], ['spentOn', 'asc']);
      break;
    case 'activity':
      sortBy.push(['activity', 'asc'], ['spentOn', 'asc']);
      break;
    default: // date
      sortBy.push(['spentOn', 'asc'], ['user', 'asc']);
  }
  params.sortBy = JSON.stringify(sortBy);

  const { json } = await opFetch<any>(env, "/api/v3/time_entries", { params });
  const meta = parseCollectionMeta(json);
  const elements = json?._embedded?.elements ?? [];
  
  // Calculate totals and analytics
  const analytics = {
    totalHours: elements.reduce((sum: number, entry: any) => sum + (entry.hours || 0), 0),
    billableHours: elements.reduce((sum: number, entry: any) => sum + (entry.billableHours || entry.hours || 0), 0),
    totalEntries: elements.length,
    averageHoursPerDay: 0,
    costTotal: elements.reduce((sum: number, entry: any) => {
      const hours = entry.hours || 0;
      const rate = entry.costRate || 0;
      return sum + (hours * rate);
    }, 0),
    billingTotal: elements.reduce((sum: number, entry: any) => {
      const hours = entry.billableHours || entry.hours || 0;
      const rate = entry.billingRate || 0;
      return sum + (hours * rate);
    }, 0),
  };

  // Calculate daily average
  const daysDiff = Math.ceil((new Date(input.endDate).getTime() - new Date(input.startDate).getTime()) / (1000 * 3600 * 24)) + 1;
  analytics.averageHoursPerDay = analytics.totalHours / daysDiff;

  // Group data if requested
  const groupedData: Record<string, any[]> = {};
  if (input.groupBy !== 'date') {
    elements.forEach((entry: any) => {
      const groupKey = entry[input.groupBy]?.name || entry[input.groupBy] || 'Unknown';
      if (!groupedData[groupKey]) {
        groupedData[groupKey] = [];
      }
      groupedData[groupKey].push(entry);
    });
  }

  return { 
    timesheet: {
      period: { startDate: input.startDate, endDate: input.endDate },
      entries: elements,
      groupedBy: input.groupBy,
      groupedData: Object.keys(groupedData).length > 0 ? groupedData : undefined,
      analytics: input.includeTotals ? analytics : undefined,
    },
    ...meta,
    appliedFilters: filters
  };
}

//
// Resource Allocation Management (Enterprise)
//
export const allocateResourceInput = z.object({
  projectId: z.union([z.string(), z.number()]).describe("Project ID"),
  userId: z.union([z.string(), z.number()]).describe("User ID to allocate"),
  
  // Resource allocation (MS Project style)
  allocationPercentage: z.number().min(0).max(200).describe("Allocation percentage (0-200%, 100% = full time)"),
  startDate: z.string().describe("Allocation start date (YYYY-MM-DD)"),
  endDate: z.string().describe("Allocation end date (YYYY-MM-DD)"),
  
  // Cost management
  standardRate: z.number().min(0).optional().describe("Standard hourly rate"),
  overtimeRate: z.number().min(0).optional().describe("Overtime hourly rate"),
  costPerUse: z.number().min(0).optional().describe("Fixed cost per assignment"),
  
  // Capacity planning
  maxUnitsAvailable: z.number().min(0).max(10).default(1).describe("Maximum units available (1.0 = 100%)"),
  workingCalendarId: z.union([z.string(), z.number()]).optional().describe("Working calendar reference"),
  
  // Role-based assignment
  roleIds: z.array(z.union([z.string(), z.number()])).optional().describe("Role IDs for this allocation"),
}).strict();

export async function allocateResource({ env }: Ctx, input: z.infer<typeof allocateResourceInput>) {
  // Create project membership with allocation details
  const membershipPayload: any = {
    _links: {
      project: hal.project(input.projectId),
      principal: hal.user(input.userId),
    },
  };

  // Add roles if specified
  if (input.roleIds && input.roleIds.length > 0) {
    membershipPayload._links.roles = input.roleIds.map(roleId => ({ href: `/api/v3/roles/${roleId}` }));
  }

  const { json: membership } = await opFetch<any>(env, "/api/v3/memberships", {
    method: "POST",
    body: JSON.stringify(membershipPayload),
  });

  // Store allocation details in custom fields (if supported)
  const allocationData = {
    allocationPercentage: input.allocationPercentage,
    startDate: input.startDate,
    endDate: input.endDate,
    standardRate: input.standardRate,
    overtimeRate: input.overtimeRate,
    costPerUse: input.costPerUse,
    maxUnitsAvailable: input.maxUnitsAvailable,
    workingCalendarId: input.workingCalendarId,
  };

  return { 
    ok: true, 
    membership,
    allocation: allocationData,
    membershipUrl: membership?._links?.self?.href,
    effectiveDates: {
      start: input.startDate,
      end: input.endDate,
    },
    capacityUtilization: `${input.allocationPercentage}%`
  };
}

//
// Resource Utilization Reporting
//
export const generateResourceUtilizationInput = z.object({
  // Time period for analysis
  startDate: z.string().describe("Analysis start date (YYYY-MM-DD)"),
  endDate: z.string().describe("Analysis end date (YYYY-MM-DD)"),
  
  // Filtering
  projectId: z.union([z.string(), z.number()]).optional().describe("Filter by project"),
  userId: z.union([z.string(), z.number()]).optional().describe("Filter by specific user"),
  departmentId: z.union([z.string(), z.number()]).optional().describe("Filter by department"),
  
  // Analysis options
  includeAllocations: z.boolean().default(true).describe("Include planned allocations"),
  includeActuals: z.boolean().default(true).describe("Include actual time logged"),
  calculateVariance: z.boolean().default(true).describe("Calculate planned vs actual variance"),
}).strict();

export async function generateResourceUtilization({ env }: Ctx, input: z.infer<typeof generateResourceUtilizationInput>) {
  // Get actual time entries for the period
  const timeFilters: Record<string, any> = {
    spentOn: { operator: ">=d", values: [input.startDate] }
  };

  if (input.endDate) {
    timeFilters.spentOnEnd = { operator: "<=d", values: [input.endDate] };
  }

  if (input.projectId) {
    timeFilters.project = { operator: "=", values: [input.projectId.toString()] };
  }

  if (input.userId) {
    timeFilters.user = { operator: "=", values: [input.userId.toString()] };
  }

  const { json: timeData } = await opFetch<any>(env, "/api/v3/time_entries", {
    params: {
      filters: JSON.stringify(timeFilters),
      pageSize: 1000, // Get comprehensive data
      sortBy: JSON.stringify([['user', 'asc'], ['spentOn', 'asc']])
    }
  });

  const timeEntries = timeData?._embedded?.elements ?? [];

  // Calculate utilization by user
  const userUtilization: Record<string, any> = {};
  
  timeEntries.forEach((entry: any) => {
    const userId = entry._links?.user?.href?.split('/').pop();
    const userName = entry._links?.user?.title || `User ${userId}`;
    
    if (!userUtilization[userId]) {
      userUtilization[userId] = {
        userId,
        userName,
        totalHours: 0,
        billableHours: 0,
        projects: {},
        dailyHours: {},
      };
    }
    
    const util = userUtilization[userId];
    util.totalHours += entry.hours || 0;
    util.billableHours += entry.billableHours || entry.hours || 0;
    
    // Track by project
    const projectId = entry._links?.project?.href?.split('/').pop();
    const projectName = entry._links?.project?.title || `Project ${projectId}`;
    
    if (!util.projects[projectId]) {
      util.projects[projectId] = { name: projectName, hours: 0 };
    }
    util.projects[projectId].hours += entry.hours || 0;
    
    // Track by day
    if (!util.dailyHours[entry.spentOn]) {
      util.dailyHours[entry.spentOn] = 0;
    }
    util.dailyHours[entry.spentOn] += entry.hours || 0;
  });

  // Calculate summary metrics
  const totalUsers = Object.keys(userUtilization).length;
  const totalHours = Object.values(userUtilization).reduce((sum: number, user: any) => sum + user.totalHours, 0);
  const averageHoursPerUser = totalUsers > 0 ? totalHours / totalUsers : 0;
  
  const daysDiff = Math.ceil((new Date(input.endDate).getTime() - new Date(input.startDate).getTime()) / (1000 * 3600 * 24)) + 1;
  const workingDays = daysDiff * 0.71; // Assume ~5 working days per week
  const expectedHoursPerUser = workingDays * 8; // 8 hours per day
  
  return {
    resourceUtilization: {
      period: { startDate: input.startDate, endDate: input.endDate },
      summary: {
        totalUsers,
        totalHours,
        averageHoursPerUser,
        workingDays,
        expectedHoursPerUser,
        utilizationRate: expectedHoursPerUser > 0 ? (averageHoursPerUser / expectedHoursPerUser) * 100 : 0,
      },
      userDetails: Object.values(userUtilization),
      periodMetrics: {
        peakDay: null, // Could calculate from daily data
        lowDay: null,
        averageDailyHours: totalHours / daysDiff,
      }
    }
  };
}

// src/tools/users.ts
import { z } from "zod";
import { opFetch, parseCollectionMeta, withQuery } from "../util/op";
import type { Ctx } from "../tools";

//
// Users: search by name or get current user
//
export const searchUsersInput = z.object({
  q: z.string().optional().describe("Search term for user name/login"),
  offset: z.number().int().min(0).default(0),
  pageSize: z.number().int().min(1).max(200).default(25),
});

export async function searchUsers({ env }: Ctx, input: z.infer<typeof searchUsersInput>) {
  const params: Record<string, unknown> = {
    offset: input.offset,
    pageSize: input.pageSize,
  };
  
  // Add name filter if search term provided
  if (input.q?.trim()) {
    params.filters = JSON.stringify([
      { name: { operator: "~", values: [input.q.trim()] } }
    ]);
  }

  const path = withQuery("/api/v3/users", params);
  const { json } = await opFetch<any>(env, path);
  const meta = parseCollectionMeta(json);
  const elements = json?._embedded?.elements ?? [];
  return { elements, ...meta };
}

//
// Get current authenticated user
//
export const getCurrentUserInput = z.object({});

export async function getCurrentUser({ env }: Ctx, _input: z.infer<typeof getCurrentUserInput>) {
  const { json } = await opFetch<any>(env, "/api/v3/users/me");
  return json;
}

// src/tools/versions.ts
import { z } from "zod";
import { opFetch, parseCollectionMeta } from "../util/op";
import type { Ctx } from "../tools";

//
// Versions: list (per-project)
//
export const listVersionsInput = z.object({
  projectId: z.union([z.string(), z.number()]),
  offset: z.number().int().min(0).default(0),
  pageSize: z.number().int().min(1).max(200).default(25),
});

export async function listVersions({ env }: Ctx, input: z.infer<typeof listVersionsInput>) {
  const { json } = await opFetch<any>(env, `/api/v3/projects/${input.projectId}/versions`, {
    params: { offset: input.offset, pageSize: input.pageSize },
  });
  const meta = parseCollectionMeta(json);
  const elements = json?._embedded?.elements ?? [];
  return { elements, ...meta };
}

// src/tools/webhooks.ts
import { z } from "zod";
import { opFetch } from "../util/op";
import type { Ctx } from "../tools";

/**
 * Enhanced Webhooks API Tools
 * 
 * Leverages OpenProject's latest webhook capabilities for real-time updates including:
 * - Work package comments and updates
 * - Time entries
 * - Attachments
 * - Project changes
 */

// Webhook event types supported by OpenProject 2024-2025
const WebhookEventSchema = z.enum([
  'work_package:created',
  'work_package:updated', 
  'work_package:deleted',
  'work_package:commented',
  'time_entry:created',
  'time_entry:updated',
  'time_entry:deleted', 
  'attachment:created',
  'attachment:deleted',
  'project:created',
  'project:updated',
  'project:deleted'
]).describe("OpenProject webhook event types");

// Create webhook configuration
export const createWebhookInput = z.object({
  name: z.string().min(1)
    .describe("Webhook name for identification"),
  url: z.string().url()
    .describe("Webhook endpoint URL to receive events"),
  events: z.array(WebhookEventSchema)
    .min(1)
    .describe("Array of events to subscribe to"),
  secret: z.string().optional()
    .describe("Secret for webhook signature verification"),
  enabled: z.boolean().default(true)
    .describe("Whether webhook is active"),
  description: z.string().optional()
    .describe("Optional description of webhook purpose"),
  projects: z.array(z.union([z.string(), z.number()])).optional()
    .describe("Specific project IDs to filter events (empty = all projects)")
}).strict();

export async function createWebhook({ env }: Ctx, input: z.infer<typeof createWebhookInput>) {
  try {
    const payload: any = {
      name: input.name,
      url: input.url,
      events: input.events,
      enabled: input.enabled,
      description: input.description || `MCP Webhook: ${input.name}`,
      secret: input.secret
    };
    
    // Add project filters if specified
    if (input.projects && input.projects.length > 0) {
      payload.projects = input.projects.map(id => ({ id: String(id) }));
    }
    
    const { json: webhook } = await opFetch<any>(env, "/api/v3/webhooks", {
      method: 'POST',
      body: JSON.stringify(payload)
    });
    
    return {
      success: true,
      webhook: {
        id: webhook.id,
        name: webhook.name,
        url: webhook.url,
        events: webhook.events,
        enabled: webhook.enabled,
        secret: !!webhook.secret, // Don't return actual secret
        createdAt: webhook.createdAt,
        _links: webhook._links
      },
      mcpIntegration: {
        realTimeUpdates: true,
        eventsSupported: input.events.length,
        projectFiltered: !!(input.projects?.length)
      }
    };
    
  } catch (error: any) {
    return {
      success: false,
      error: `Failed to create webhook: ${error.message}`,
      suggestion: "Ensure you have admin permissions and the webhook URL is accessible"
    };
  }
}

// List existing webhooks
export const listWebhooksInput = z.object({
  includeDisabled: z.boolean().default(false)
    .describe("Include disabled webhooks in results"),
  pageSize: z.number().int().min(1).max(100).default(20),
  offset: z.number().int().min(0).default(0)
}).strict();

export async function listWebhooks({ env }: Ctx, input: z.infer<typeof listWebhooksInput>) {
  try {
    const params: any = {
      pageSize: input.pageSize,
      offset: input.offset
    };
    
    // Filter by enabled status if needed
    if (!input.includeDisabled) {
      params.filters = JSON.stringify([{
        enabled: { operator: '=', values: ['true'] }
      }]);
    }
    
    const { json } = await opFetch<any>(env, "/api/v3/webhooks", { params });
    
    const webhooks = (json?._embedded?.elements || []).map((webhook: any) => ({
      id: webhook.id,
      name: webhook.name,
      url: webhook.url,
      events: webhook.events || [],
      enabled: webhook.enabled,
      hasSecret: !!webhook.secret,
      description: webhook.description,
      createdAt: webhook.createdAt,
      updatedAt: webhook.updatedAt,
      projectFilters: webhook.projects || [],
      _links: webhook._links
    }));
    
    return {
      webhooks,
      total: json?.total || 0,
      count: webhooks.length,
      pageSize: input.pageSize,
      offset: input.offset,
      mcpInfo: {
        realTimeCapable: webhooks.length > 0,
        eventsConfigured: webhooks.reduce((sum: number, w: any) => sum + w.events.length, 0)
      }
    };
    
  } catch (error: any) {
    return {
      error: `Failed to list webhooks: ${error.message}`,
      webhooks: []
    };
  }
}

// Update webhook configuration
export const updateWebhookInput = z.object({
  webhookId: z.union([z.string(), z.number()])
    .describe("Webhook ID to update"),
  name: z.string().optional()
    .describe("Updated webhook name"),
  url: z.string().url().optional()
    .describe("Updated webhook URL"),
  events: z.array(WebhookEventSchema).optional()
    .describe("Updated events to subscribe to"),
  enabled: z.boolean().optional()
    .describe("Enable/disable webhook"),
  secret: z.string().optional()
    .describe("Updated secret for signature verification"),
  description: z.string().optional()
    .describe("Updated description"),
  projects: z.array(z.union([z.string(), z.number()])).optional()
    .describe("Updated project filters")
}).strict();

export async function updateWebhook({ env }: Ctx, input: z.infer<typeof updateWebhookInput>) {
  try {
    // Build update payload with only provided fields
    const payload: any = {};
    
    if (input.name !== undefined) {payload.name = input.name;}
    if (input.url !== undefined) {payload.url = input.url;}
    if (input.events !== undefined) {payload.events = input.events;}
    if (input.enabled !== undefined) {payload.enabled = input.enabled;}
    if (input.secret !== undefined) {payload.secret = input.secret;}
    if (input.description !== undefined) {payload.description = input.description;}
    if (input.projects !== undefined) {
      payload.projects = input.projects.map(id => ({ id: String(id) }));
    }
    
    const { json: webhook } = await opFetch<any>(env, `/api/v3/webhooks/${input.webhookId}`, {
      method: 'PATCH',
      body: JSON.stringify(payload)
    });
    
    return {
      success: true,
      webhook: {
        id: webhook.id,
        name: webhook.name,
        url: webhook.url,
        events: webhook.events,
        enabled: webhook.enabled,
        hasSecret: !!webhook.secret,
        updatedAt: webhook.updatedAt,
        _links: webhook._links
      }
    };
    
  } catch (error: any) {
    return {
      success: false,
      error: `Failed to update webhook: ${error.message}`,
      webhookId: input.webhookId
    };
  }
}

// Delete webhook
export const deleteWebhookInput = z.object({
  webhookId: z.union([z.string(), z.number()])
    .describe("Webhook ID to delete")
}).strict();

export async function deleteWebhook({ env }: Ctx, input: z.infer<typeof deleteWebhookInput>) {
  try {
    await opFetch<any>(env, `/api/v3/webhooks/${input.webhookId}`, {
      method: 'DELETE'
    });
    
    return {
      success: true,
      webhookId: input.webhookId,
      deletedAt: new Date().toISOString()
    };
    
  } catch (error: any) {
    return {
      success: false,
      error: `Failed to delete webhook: ${error.message}`,
      webhookId: input.webhookId
    };
  }
}

// Test webhook delivery
export const testWebhookInput = z.object({
  webhookId: z.union([z.string(), z.number()])
    .describe("Webhook ID to test"),
  eventType: WebhookEventSchema.optional()
    .describe("Specific event type to test (defaults to work_package:updated)")
}).strict();

export async function testWebhook({ env }: Ctx, input: z.infer<typeof testWebhookInput>) {
  try {
    const testPayload = {
      eventType: input.eventType || 'work_package:updated',
      test: true
    };
    
    const { json: result } = await opFetch<any>(env, `/api/v3/webhooks/${input.webhookId}/test`, {
      method: 'POST',
      body: JSON.stringify(testPayload)
    });
    
    return {
      success: true,
      webhookId: input.webhookId,
      testResult: result,
      deliveryStatus: result?.delivered ? 'success' : 'failed',
      responseCode: result?.responseCode,
      responseTime: result?.responseTime,
      testedAt: new Date().toISOString()
    };
    
  } catch (error: any) {
    return {
      success: false,
      error: `Failed to test webhook: ${error.message}`,
      webhookId: input.webhookId
    };
  }
}

// Get webhook delivery logs
export const getWebhookLogsInput = z.object({
  webhookId: z.union([z.string(), z.number()])
    .describe("Webhook ID to get logs for"),
  limit: z.number().int().min(1).max(100).default(20)
    .describe("Number of recent deliveries to retrieve"),
  successOnly: z.boolean().default(false)
    .describe("Show only successful deliveries")
}).strict();

export async function getWebhookLogs({ env }: Ctx, input: z.infer<typeof getWebhookLogsInput>) {
  try {
    const params: any = { limit: input.limit };
    
    if (input.successOnly) {
      params.filters = JSON.stringify([{
        success: { operator: '=', values: ['true'] }
      }]);
    }
    
    const { json } = await opFetch<any>(env, `/api/v3/webhooks/${input.webhookId}/deliveries`, { params });
    
    const deliveries = (json?._embedded?.elements || []).map((delivery: any) => ({
      id: delivery.id,
      eventType: delivery.eventType,
      deliveredAt: delivery.deliveredAt,
      success: delivery.success,
      responseCode: delivery.responseCode,
      responseTime: delivery.responseTime,
      retryCount: delivery.retryCount || 0,
      errorMessage: delivery.errorMessage
    }));
    
    // Calculate delivery statistics
    const stats = {
      total: deliveries.length,
      successful: deliveries.filter((d: any) => d.success).length,
      failed: deliveries.filter((d: any) => !d.success).length,
      averageResponseTime: deliveries.reduce((sum: number, d: any) => sum + (d.responseTime || 0), 0) / deliveries.length || 0
    };
    
    return {
      webhookId: input.webhookId,
      deliveries,
      statistics: stats,
      realTimePerformance: {
        successRate: stats.total > 0 ? (stats.successful / stats.total) * 100 : 0,
        reliability: stats.successful > stats.failed ? 'good' : 'needs_attention',
        averageLatency: Math.round(stats.averageResponseTime)
      }
    };
    
  } catch (error: any) {
    return {
      error: `Failed to get webhook logs: ${error.message}`,
      webhookId: input.webhookId
    };
  }
}

// Validate webhook signature (for incoming webhook handling)
export const validateWebhookSignatureInput = z.object({
  payload: z.string()
    .describe("Webhook payload as string"),
  signature: z.string()
    .describe("Webhook signature from header"),
  secret: z.string()
    .describe("Webhook secret for validation")
}).strict();

export async function validateWebhookSignature({ env }: Ctx, input: z.infer<typeof validateWebhookSignatureInput>) {
  try {
    // OpenProject uses HMAC-SHA256 for webhook signatures
    // Use Web Crypto API (available in Cloudflare Workers)
    const encoder = new TextEncoder();
    const key = await crypto.subtle.importKey(
      'raw',
      encoder.encode(input.secret),
      { name: 'HMAC', hash: 'SHA-256' },
      false,
      ['sign']
    );
    
    const signature = await crypto.subtle.sign('HMAC', key, encoder.encode(input.payload));
    const expectedSignature = Array.from(new Uint8Array(signature))
      .map(b => b.toString(16).padStart(2, '0'))
      .join('');
    
    const providedSignature = input.signature.replace('sha256=', '');
    
    // Constant-time comparison
    let isValid = expectedSignature.length === providedSignature.length;
    for (let i = 0; i < expectedSignature.length; i++) {
      isValid = isValid && (expectedSignature[i] === providedSignature[i]);
    }
    
    return {
      valid: isValid,
      algorithm: 'HMAC-SHA256',
      providedSignature,
      expectedSignature: isValid ? 'matches' : 'mismatch',
      securityNote: 'Always validate webhook signatures in production',
      environment: 'Cloudflare Workers compatible'
    };
    
  } catch (error: any) {
    return {
      valid: false,
      error: `Signature validation failed: ${error.message}`,
      algorithm: 'HMAC-SHA256'
    };
  }
}

// src/tools/workPackagesEnterprise.ts
import { z } from "zod";
import { opFetch, parseCollectionMeta, hal, withQuery } from "../util/op";
import type { Ctx } from "../tools";

//
// Enterprise Work Package Management - MS Project/PMBOK Level
//

// Duration schema (ISO 8601 duration format: PT8H = 8 hours, PT2D = 2 days)
const DurationSchema = z.string().regex(/^PT\d+[HDWMY]$/).optional().describe("ISO 8601 duration (PT8H, PT2D, etc.)");

// Formattable field schema
const FormattableFieldSchema = z.object({
  format: z.string().default("markdown"),
  raw: z.string(),
  html: z.string().optional(),
}).optional();

// Enterprise custom fields (30+ supported)
const WorkPackageCustomFieldsSchema = z.object({
  customField1: z.any().optional(), // WBS code
  customField2: z.any().optional(), // Resource pool
  customField3: z.any().optional(), // Cost center
  customField4: z.any().optional(), // Risk level
  customField5: z.any().optional(), // Quality gate
  customField6: z.any().optional(), // Client billable
  customField7: z.any().optional(), // Department
  customField8: z.any().optional(), // Technology stack
  customField9: z.any().optional(), // Complexity rating
  customField10: z.any().optional(), // Business value
}).partial();

// Relation types for dependencies
const RelationTypeSchema = z.enum([
  'follows',      // Finish-Start (FS)
  'blocks',       // Start-Start (SS) 
  'precedes',     // Finish-Finish (FF)
  'duplicates',   // Duplicate relationship
  'relates'       // General relationship
]);

//
// Enterprise Work Package Creation with Full Scheduling
//
export const createWorkPackageEnterpriseInput = z.object({
  // Core required fields
  projectId: z.union([z.string(), z.number()]).describe("Project ID"),
  typeId: z.union([z.string(), z.number()]).describe("Work package type ID"),
  subject: z.string().min(1).max(255).describe("Work package subject/title"),
  
  // Complete scheduling schema (MS Project equivalent)
  description: FormattableFieldSchema.describe("Detailed description with markdown"),
  startDate: z.string().optional().describe("Planned start date (YYYY-MM-DD)"),
  dueDate: z.string().optional().describe("Planned due date (YYYY-MM-DD)"),
  estimatedTime: DurationSchema.describe("Estimated work effort (PT8H, PT2D)"),
  percentageDone: z.number().int().min(0).max(100).optional().describe("Completion percentage 0-100"),
  scheduleManually: z.boolean().default(false).describe("Manual vs automatic scheduling"),
  
  // Resource management (enterprise)
  assigneeId: z.union([z.string(), z.number()]).optional().describe("Primary assignee user ID"),
  responsibleId: z.union([z.string(), z.number()]).optional().describe("Responsible person (RACI)"),
  
  // Project structure
  parentId: z.union([z.string(), z.number()]).optional().describe("Parent work package (WBS hierarchy)"),
  versionId: z.union([z.string(), z.number()]).optional().describe("Target milestone/version"),
  
  // Status and priority
  statusId: z.union([z.string(), z.number()]).optional().describe("Work package status"),
  priorityId: z.union([z.string(), z.number()]).optional().describe("Priority level"),
  
  // Enterprise project management
  budgetId: z.union([z.string(), z.number()]).optional().describe("Associated budget"),
  categoryId: z.union([z.string(), z.number()]).optional().describe("Work category"),
  
  // Custom enterprise fields
  customFields: WorkPackageCustomFieldsSchema.optional().describe("Organization-specific fields"),
  
  // Notification and validation
  sendNotifications: z.boolean().default(true).describe("Send stakeholder notifications"),
  dryRun: z.boolean().default(false).describe("Validate only without creating"),
}).strict();

export async function createWorkPackageEnterprise({ env }: Ctx, input: z.infer<typeof createWorkPackageEnterpriseInput>) {
  // Build comprehensive work package payload
  const payload: any = {
    subject: input.subject,
    _links: {
      project: hal.project(input.projectId),
      type: hal.type(input.typeId),
    },
  };

  // Add scheduling fields
  if (input.description) {payload.description = input.description;}
  if (input.startDate) {payload.startDate = input.startDate;}
  if (input.dueDate) {payload.dueDate = input.dueDate;}
  if (input.estimatedTime) {payload.estimatedTime = input.estimatedTime;}
  if (input.percentageDone !== undefined) {payload.percentageDone = input.percentageDone;}
  if (input.scheduleManually !== undefined) {payload.scheduleManually = input.scheduleManually;}

  // Resource assignments
  if (input.assigneeId) {payload._links.assignee = hal.user(input.assigneeId);}
  if (input.responsibleId) {payload._links.responsible = hal.user(input.responsibleId);}

  // Project structure
  if (input.parentId) {payload._links.parent = hal.workPackage(input.parentId);}
  if (input.versionId) {payload._links.version = { href: `/api/v3/versions/${input.versionId}` };}

  // Status and priority
  if (input.statusId) {payload._links.status = hal.status(input.statusId);}
  if (input.priorityId) {payload._links.priority = hal.priority(input.priorityId);}

  // Enterprise fields
  if (input.budgetId) {payload._links.budget = { href: `/api/v3/budgets/${input.budgetId}` };}
  if (input.categoryId) {payload._links.category = { href: `/api/v3/categories/${input.categoryId}` };}

  // Custom fields
  if (input.customFields) {
    Object.entries(input.customFields).forEach(([key, value]) => {
      if (value !== undefined) {
        payload[key] = value;
      }
    });
  }

  // Forms-first validation
  const { json: form } = await opFetch<any>(env, "/api/v3/work_packages/form", {
    method: "POST",
    body: JSON.stringify({ 
      ...payload, 
      _meta: { sendNotifications: input.sendNotifications } 
    }),
  });

  const errors = form?.validationErrors ?? {};
  const hasErrors = errors && Object.keys(errors).length > 0;
  
  if (input.dryRun || hasErrors) {
    return { 
      ok: !hasErrors, 
      validationErrors: errors, 
      payload: form?.payload,
      schema: form?.schema,
      warnings: form?.warnings 
    };
  }

  // Commit creation
  const commit = form?._links?.commit;
  if (!commit?.href) {throw new Error("Form commit link missing");}
  
  const { json: created } = await opFetch<any>(env, commit.href, {
    method: commit.method || "POST",
    body: JSON.stringify(form.payload),
  });

  return { 
    ok: true, 
    workPackage: created,
    workPackageUrl: created?._links?.self?.href 
  };
}

//
// Enterprise Work Package Updates with Complete Scheduling Control
//
export const updateWorkPackageEnterpriseInput = z.object({
  id: z.union([z.string(), z.number()]).describe("Work package ID"),
  lockVersion: z.number().int().min(0).describe("Lock version for optimistic locking"),
  
  // All updatable fields
  subject: z.string().min(1).max(255).optional(),
  description: FormattableFieldSchema,
  
  // Complete scheduling updates
  startDate: z.string().optional(),
  dueDate: z.string().optional(),
  estimatedTime: DurationSchema,
  percentageDone: z.number().int().min(0).max(100).optional(),
  scheduleManually: z.boolean().optional(),
  
  // Resource updates
  assigneeId: z.union([z.string(), z.number()]).optional(),
  responsibleId: z.union([z.string(), z.number()]).optional(),
  
  // Structure updates
  parentId: z.union([z.string(), z.number()]).optional(),
  versionId: z.union([z.string(), z.number()]).optional(),
  
  // Status updates
  statusId: z.union([z.string(), z.number()]).optional(),
  priorityId: z.union([z.string(), z.number()]).optional(),
  
  // Enterprise updates
  budgetId: z.union([z.string(), z.number()]).optional(),
